mapper.select:
SELECT * FROM (
SELECT Tb.*, rownum rNum FROM (
SELECT * FROM board ORDER BY idx DESC
) Tb
)
WHERE rNum BETWEEN #{param1} AND #{param2}

SELECT * FROM (
SELECT Tb.*, rownum rNum FROM (
SELECT * FROM board

	WHERE ${keyField} like '%'||#{keyString}||'%'

ORDER BY idx DESC
) Tb
)
WHERE rNum BETWEEN ${start} AND ${end}
